class  animal:
    def __init__(self,steps,speak):
        self.steps=steps
        self.speak=speak
    def walk(self):
        return self.steps +" takes"
    def talk(self):
        return self.speak+" hello"
    def typeofeye(self,eye):
        self.eye=eye
        return self.eye+" shape"


dog=animal("10","Dungen!")
print(dog.walk())
print(dog.talk())
print(dog.typeofeye("round"))

